import React from 'react'
import {useState} from 'react';
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import TextField from '@mui/material/TextField';
import Divider from '@mui/material/Divider';


export default function Edit({post, setPosts}) {

    const [open, setOpen] = React.useState(false);
    const [title, setTitle] = useState(post.title);
    const [body, setBody] = useState(post.body);
    const theme = useTheme();
    const fullScreen = useMediaQuery(theme.breakpoints.down('md'));

    const handleClickOpen = () => {
        setOpen(true);
      };

      const handleCloseExit = () => {
        setOpen(false);
      };

      const handleClose = () => {
            setOpen(false);

            if (onSaveChanges) {
                setPosts((prevTableData) => {
                    const cloneTableData = [...prevTableData];

                    cloneTableData[post.id].title = title;
                    cloneTableData[post.id].body = body;

                    return cloneTableData;
                }); 
            }
        };

  return (
    <div>
    <Button variant="outlined" color='inherit' onClick={handleClickOpen}>Edit</Button>
    <Dialog
      fullScreen={fullScreen}
      open={open}
      onClose={handleClose}
      aria-labelledby="responsive-dialog-title"
      fullWidth
      maxWidth="sm"
    >
      <DialogTitle id="responsive-dialog-title">{"Edit Post"}</DialogTitle>
      <DialogContent>
      <Divider variant="middle"/>
        <DialogContentText>
          Title
        </DialogContentText>
        <TextField id="outlined-basic" onChange={(e) => setTitle(e.target.value)} value={title} style={{ width: 500 }} variant="outlined" defaultValue={post.title}/>
        <DialogContentText>
          Description
        </DialogContentText>
        <TextField
            id="outlined-basic"
            multiline
            rows={4}
            value={body}
            onChange={(e) => setBody(e.target.value)}
            style={{ width: 500 }}
            variant="outlined"
        />
        </DialogContent>
        <DialogActions>
        <Button autoFocus variant="outlined" color='error' onClick={handleCloseExit}>
          Exit
        </Button>
        <Button color='success' variant="outlined" onClick={handleClose} autoFocus>
          Save Changes
        </Button>
      </DialogActions>
    </Dialog>
    </div>
    );
}
