import React, { useState } from 'react'
import TextField from '@mui/material/TextField';
import '../App.css';
import Button from '@mui/material/Button';
import { NavigateNextOutlined } from '@mui/icons-material';

export default function AddData({closeAddModal}) {

  const [addData, setAddData] = useState({
    title: ' ', 
    body: ' '
  })

  const handleAddDataChange = (event) => {
    event.preventDefault();
    const fieldName = event.target.getAttribute('name');
    const fieldValue = event.target.value;

    const newAddedData = {...addData};
    newAddedData[fieldName] = fieldValue;

    setAddData(newAddedData);
  }

  const handleAddSubmit = (event) => {
    event.preventDefault();

    const newRow = {
      id: nanoid(),
      title: addData.title,
      body: addData.description
    }
  }
  return (
    <div className='modalBackground'>
    <div className='modalContainer'>
      <h1> Add new data</h1>
      <TextField id="demo-helper-text-misaligned-no-helper" label="Title" name='title' onChange={handleAddDataChange} sx={{ width: '41ch' }} />
      <br></br>
      <br></br>
      <TextField id="demo-helper-text-misaligned-no-helper" label="Description" name='body' onChange={handleAddDataChange} sx={{ width: '41ch' }} />
      <div className='buttons'>
        <Button variant="contained" color="inherit">Add</Button>
        <Button variant="outlined" color="inherit" onClick={() => closeAddModal(false)}>Cancel</Button>
      </div>
    </div>
    </div>
  )
}
