import { Table, TableBody, TableHead, TableRow } from '@mui/material';
import React from 'react';
import '../App.css';
import {useState,useEffect} from 'react';
import { styled } from '@mui/material/styles';
import TableCell, { tableCellClasses } from '@mui/material/TableCell';

export default function Posts() {

    // Material UI styling
    const StyledTableCell = styled(TableCell)(({ theme }) => ({
        [`&.${tableCellClasses.head}`]: {
          backgroundColor: theme.palette.common.black,
          color: theme.palette.common.white,
        },
        [`&.${tableCellClasses.body}`]: {
          fontSize: 14,
        },
      }));

      const StyledTableRow = styled(TableRow)(({ theme }) => ({
        '&:nth-of-type(odd)': {
          backgroundColor: theme.palette.action.hover,
        },
        // hide last border
        '&:last-child td, &:last-child th': {
          border: 0,
        },
      }));


    const [posts, setPosts] = useState([]);

    useEffect(() => {
        fetch("https://jsonplaceholder.typicode.com/posts")
        .then(res => res.json())
        .then(data => setPosts(data))
    }, []);

  return (
    <Table>
        <TableHead>
            <StyledTableRow>
                <StyledTableCell><b>Title</b></StyledTableCell>
                <StyledTableCell className='column-2'><b>Description</b></StyledTableCell>
                <StyledTableCell><b>Actions</b></StyledTableCell>
            </StyledTableRow>
        </TableHead>
        <TableBody>
            {posts.map(post => (
                <StyledTableRow>
                    <StyledTableCell>{post.title}</StyledTableCell>
                    <StyledTableCell className='column-2'>{post.body}</StyledTableCell>
                    <StyledTableCell></StyledTableCell>
                </StyledTableRow>
            ))}
        </TableBody>
    </Table>
  );
}
